import React from 'react';

class Login extends React.Component {

  // State

  // Refs
  // 可以访问dom组件和自定义组件
  // ref属性不能用在函数式声明的组件上(Header)，因为函数声明的组件没有实例
  emailRef = React.createRef();
  passwordRef = React.createRef();

  handleSubmit = event => {
    // 1. 阻止默认事件行为
    event.preventDefault();
    // 2. 获取表单数据
    const formData = {
      email: this.emailRef.current.value,
      password: this.passwordRef.current.value
    }
    console.log(formData);
    // 3. 处理登录逻辑

    // 4. 跳转到首页视图
    // this.props.history.push('/');
  }

  render() {
    return (
      <div className="login-wrapper"> 
        <form action="" className="box login-box" onSubmit={this.handleSubmit}>
          <div className="field">
            <label className="label">Name</label>
            <div className="control">
            <input class="input" type="text" placeholder="Name" />
          </div>
        </div>  
          <div className="field">
            <label className="label">Email</label>
            < div className="control">
              <input class="input" type="text" placeholder="Email" ref={this.emailRef} />
            </div>
         </div>
          <div className="field">
            <label className="label">Password</label>
            <div className="control">
              <input class="input" type="text" placeholder="Password" ref={this.passwordRef} />
            </div>
          </div>
          <div className="control">
            <button className="button is-fullwidth is-primary">Login</button>
          </div>
        </form>
      </div>
    );
  }
}

 export default Login
