import React, { useMemo } from 'react';
import Header from 'components/Header';

//函数式组件
const Layout = props => {

  const user = useMemo(() => {
    return global.auth.getUser() || {};
  }, []);

  return (
    <div className="main">
      <Header user={user} />
      {props.children} 
    </div>
  );
};

export default Layout;
