import React from 'react';
import ToolBox from 'components/ToolBox';
import Product from 'components/Product';

class Products extends React.Component {

  products = [
    { 
      id: 1,
      name: 'Air Jordan 4',
      image: 'images/1.jpg',
      tag: '45 colors',
      price: '13000',
      status: 'available'
    },
    { 
      id: 2,
      name: 'Air Jordan 5',
      image: 'images/2.jpg',
      tag: '45 colors',
      price: '14000',
      status: 'available'
    },
    { 
      id: 3,
      name: 'Air Jordan 6',
      image: 'images/3.jpg',
      tag: '45 colors',
      price: '21000',
      status: 'available'
    },
    { 
      id: 4,
      name: 'Air Jordan 7',
      image: 'images/4.jpg',
      tag: '45 colors',
      price: '53000',
      status: 'available'
    },
    { 
      id: 5,
      name: 'Air Jordan 14',
      image: 'images/5.jpg',
      tag: '45 colors',
      price: '33000',
      status: 'unavailable'
    }
  ]

  // 生命周期函数，初次渲染完成执行
  componentDidMount() {
    fetch('http://localhost:3003/products')
      .then(response => response.json())
      .then(data => {
        console.log(data)
      }) 
  }

  render() {
    return (
      <div>
        <ToolBox />
        <div className="products">
          <div className="columns is-multiline is-desktop">
            {
              this.products.map(p => {
                return (
                  <div className="column is-3">
                    <Product product={p} />
                  </div>
                )
              })
            }
          </div>
        </div>
      </div>
    )
  }
} 

export default Products;
