import React, { useState, useMemo } from 'react';
import { formatPrice } from 'commons/helper';

const CartItem = props => {

  const { name, image, price, amount } = props.cart || {}
  const sumPrice = formatPrice(amount * price);

  return (
    <div className="columns is-vcentered">
      <div className="column is-narrow">
        <span className="close">X</span>
      </div>
      <div className="column is-narrow">
        <img src={image} alt={name} width="100" />
      </div>
      <div className="column cart-name is-narrow">
        {name}
      </div>
      <div className="column">
        <span className="price">{formatPrice(price)}</span>
      </div>
      <div className="column">
        <input type="number" className="imput num-input" defaultValue={amount} />
      </div>
      <div className="column">
        <span className="sum-price">{sumPrice}</span>
      </div>
    </div>
  )
} 

export default CartItem;
