import React from 'react';
import axios from 'commons/axios';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import ToolBox from 'components/ToolBox';
import Product from 'components/Product';
import Panel from 'components/Panel';
import AddInventory from 'components/AddInventory';



class Products extends React.Component {

  state = {
    // products是一个数组
    products: [],
    sourceProducts: []
  };

  // 生命周期函数，初次渲染完成执行
  componentDidMount() {
    axios.get('/products')
      .then(response => {
        this.setState({
          products: response.data,
          sourceProducts: response.data
        });
      })
  }

  search = text => {
    console.log(text);
    // 1. Copy，如果使用 const _products = this.state.products，原数组也会改变
    let _products = [...this.state.sourceProducts]
    // 2. Filter
    _products = _products.filter(p => {
      const matchArray = p.name.match(new RegExp(text, 'gi'))
      return !!matchArray
    })
    // 3. Set State
    this.setState({
      products: _products
    });
  }

  toAdd = () => {
    Panel.open({
      component: AddInventory,
      callback: data => {
        if (data) {
          this.add(data);
        }
        console.log(data + 'in products component');
      }
    });
  }

  add = product => {
    const _products = [...this.state.products]
    _products.push(product)
    const _sProducts = [...this.state.sourceProducts]
    _sProducts.push(product)
    this.setState({
      products: _products,
      sourceProducts: _sProducts
    })
  }

  update = product => {
    const _products = [...this.state.products]
    const _index = _products.findIndex(p => p.id === product.id)
    //splice函数替换数组中某一项
    _products.splice(_index, 1, product)
    const _sProducts = [...this.state.sourceProducts]
    const _sIndex = _products.findIndex(p => p.id === product.id)
    _sProducts.slice(_sIndex, 1, product)
    this.setState({
      products: _products,
      sourceProducts: _sProducts
    })
  }


  //什么时候重新执行render函数: state或props变化
  render() {
    return (
      <div>
        <ToolBox search={this.search} />
        <div className="products">
          <div className="columns is-multiline is-desktop">
            <TransitionGroup component={null}>
              {
                this.state.products.map(p => {
                  return (
                    <CSSTransition classNames="product-fade" timeout={{enter: 300, exit: 300}} key={p.id}>
                      <div className="column is-3" key={p.id}>
                        <Product product={p} update={this.update} />
                      </div>
                    </CSSTransition>
                  )
                })
              }
            </TransitionGroup>
          </div>
          <button className="button is-primary add-btn" onClick={this.toAdd}>add</button>
        </div>
      </div>
    )
  }
} 

export default Products;
